DROP TABLE IF EXISTS shark;
CREATE TABLE shark(
   season_number                      INTEGER  NOT NULL
  ,episode_number                     INTEGER  NOT NULL
  ,pitch_number                       INTEGER  NOT NULL
  ,original_air_date                  DATE  NOT NULL
  ,startup_name                       VARCHAR(32) NOT NULL
  ,industry                           VARCHAR(23) NOT NULL
  ,business_description               VARCHAR(92) NOT NULL
  ,pitchers_gender                    VARCHAR(10)
  ,pitchers_city                      VARCHAR(18)
  ,pitchers_state                     VARCHAR(6)
  ,pitchers_avg_age                   VARCHAR(6)
  ,entrepreneur_names                 VARCHAR(71)
  ,company_website                    VARCHAR(65)
  ,multiple_entrepreneurs             BIT 
  ,us_viewership                      NUMERIC(4,2) NOT NULL
  ,original_ask_amount                INTEGER  NOT NULL
  ,original_offered_equity            NUMERIC(4,1) NOT NULL
  ,valuation_requested                INTEGER  NOT NULL
  ,got_deal                           BIT  NOT NULL
  ,total_deal_amount                  INTEGER 
  ,total_deal_equity                  NUMERIC(5,2)
  ,deal_valuation                     INTEGER 
  ,num_sharks_deal                    INTEGER 
  ,investment_per_shark               NUMERIC(12,5)
  ,equity_per_shark                   NUMERIC(12,9)
  ,royalty_deal                       BIT 
  ,loan                               INTEGER 
  ,Barbara_Corcoran_Investment_Amount INTEGER 
  ,Barbara_Corcoran_Investment_Equity NUMERIC(4,1)
  ,Mark_Cuban_Investment_Amount       NUMERIC(12,5)
  ,Mark_Cuban_Investment_Equity       NUMERIC(11,8)
  ,Lori_Greiner_Investment_Amount     NUMERIC(12,5)
  ,Lori_Greiner_Investment_Equity     NUMERIC(6,3)
  ,Robert_Herjavec_Investment_Amount  NUMERIC(12,5)
  ,Robert_Herjavec_Investment_Equity  NUMERIC(11,8)
  ,Daymond_John_Investment_Amount     NUMERIC(10,3)
  ,Daymond_John_Investment_Equity     NUMERIC(6,3)
  ,Kevin_O_Leary_Investment_Amount    NUMERIC(12,5)
  ,Kevin_O_Leary_Investment_Equity    NUMERIC(11,8)
  ,Guest_Investment_Amount            NUMERIC(12,5)
  ,Guest_Investment_Equity            NUMERIC(5,2)
  ,GuestName                          VARCHAR(17)
  ,Barbara_Corcoran_Present           BIT 
  ,Mark_Cuban_Present                 BIT 
  ,Lori_Greiner_Present               BIT 
  ,Robert_Herjavec_Present            BIT 
  ,Daymond_John_Present               BIT 
  ,Kevin_O_Leary_Present              BIT 
);


DROP TABLE IF EXISTS Episode;
CREATE TABLE Episode AS
SELECT season_number, 
	episode_number, 
	pitch_number, 
	original_air_date, 
	us_viewership
FROM shark;

DROP TABLE IF EXISTS Startup;
CREATE TABLE Startup AS
SELECT startup_name, 
	industry, 
	business_description, 
    entrepreneur_names, 
    pitchers_city, 
    pitchers_state, 
    company_website, 
    pitch_number
FROM shark;

DROP TABLE IF EXISTS Entrepreneur;
CREATE TABLE Entrepreneur AS
SELECT entrepreneur_names, 
	pitch_number, 
    pitchers_gender, 
    startup_name, 
    pitchers_avg_age, 
    multiple_entrepreneurs
FROM shark;

DROP TABLE IF EXISTS Deal;
CREATE TABLE Deal AS
SELECT pitch_number, startup_name, original_ask_amount, original_offered_equity, valuation_requested, got_deal, 
	IFNULL(total_deal_amount, 0) AS total_deal_amount, 
    IFNULL(total_deal_equity, 0) AS total_deal_equity, 
    IFNULL(deal_valuation, 0) AS deal_valuation, 
    IFNULL(num_sharks_deal, 0) AS num_sharks_deal, 
    IFNULL(investment_per_shark, 0) AS investment_per_shark, 
    IFNULL(equity_per_shark, 0) AS equity_per_shark, 
    IFNULL(royalty_deal, 0) AS royalty_deal, 
    IFNULL(loan, 0) AS loan
FROM shark;

DROP TABLE IF EXISTS Barbara;
CREATE TABLE Barbara AS
SELECT season_number, 
	episode_number, 
    pitch_number, 
	IFNULL(Barbara_Corcoran_Investment_Amount, 0) AS BC_Investment_Amount, 
	IFNULL(Barbara_Corcoran_Investment_Equity, 0) AS BC_Investment_Equity,
	Barbara_Corcoran_Present AS BC_Present
FROM shark;

